import React from "react";

import {
	Addressbox,
	ChangeButton,
	DivMar5,
	FirstRow,
	HeaderLeft,
	HeaderText,
	LeftOuterMost,
	LeftRoot,
	Lightcolor,
	ReturnBody,
	SummaryOuter,
} from "../styles";

const ReturnAdd = ({
	children,
	dataLocale = {
		CHANGE: "CHANGE",
		PICKUP_ADDRESS: "PICKUP ADDRESS",
		PICKUP_SCHEDULED: "Pickup Scheduled:",
		HEADER: "2. How do you wish to return the item?"
	},
	nearByAddress,
	returnAddress,
	OpenSidebar,
	isExpandable,
	setIsExpandable,
}: any) => {
  

	return (
		<LeftOuterMost>
			<LeftRoot>
				<HeaderLeft background={isExpandable !== "RETURN_ADD" ? false : true}>
					<HeaderText>{dataLocale.HEADER}</HeaderText>
					{isExpandable !== "RETURN_ADD" ? (
						<ChangeButton onClick={() => setIsExpandable("RETURN_ADD")}>
							{dataLocale.CHANGE}
						</ChangeButton>
					) : null}
				</HeaderLeft>
			</LeftRoot>
			{isExpandable === "RETURN_ADD" ? (
				<ReturnBody>
					<Addressbox>
						<FirstRow>
							<div>{dataLocale.PICKUP_ADDRESS}</div>
							<div onClick={() => OpenSidebar()}>{dataLocale.CHANGE}</div>
						</FirstRow>
						<DivMar5>
							{returnAddress.firstName + " " + returnAddress.lastName}
						</DivMar5>
						<Lightcolor>{nearByAddress}</Lightcolor>
					</Addressbox>
					{children}
				</ReturnBody>
			) : (
				<SummaryOuter>
					<div>
						{dataLocale.PICKUP_SCHEDULED} <Lightcolor>{nearByAddress}</Lightcolor>
					</div>
				</SummaryOuter>
			)}
		</LeftOuterMost>
	);
};

export { ReturnAdd };