import React, { useRef } from "react";
import { PaymentAnimationLottieTypes } from "./PaymentAnimationLottie.types";
import Lottie from "lottie-react";
import AnimationLottie from "../../../../../../assets/Animation/animation.json";
const options = {
	loop: false,
	autoplay: true,
	animationData: AnimationLottie,
};
const style = {
	margin: "-2px",
	borderRadius:"50%",
};

const PaymentAnimationLottie = ({
	getCallbackValue
}: PaymentAnimationLottieTypes) => {

	const ref = useRef(null);
	return <div>
		<Lottie
			lottieRef={ref}
			{...options}
			style={style}
			onComplete={() => {
				getCallbackValue(true);
			}}
		/>
	</div>;
};

export { PaymentAnimationLottie };
