import React from "react";
import Button from "../../Button";
import { DeleteAddressModalType } from "./DeleteAddressModal.types";
import { DeleteAddressModalContainer, ButtonWrapper, Title, Description, ContentWrapper } from "./styles";
import { kindENUM, ThemeENUM } from "../../../../Types/general";

const DeleteAddressModal = (props:DeleteAddressModalType)=>{
	const { deleteAddress, dataLocale, font, cancel } = props;
	return(
		<DeleteAddressModalContainer styleFont={font} >
			<ContentWrapper>
				<Title styleFont={font}>{dataLocale?.DELETE_ADDRESS_QUESTION_MARK}</Title>
				<Description styleFont={font}>{dataLocale?.ARE_YOU_SURE_YOU_WANT_TO_DELETE_ADDRESS}</Description>
			</ContentWrapper>
			<ButtonWrapper>
				<Button
					font={font}
					id="delete-address"
					kind={kindENUM.background}
					onClick={() => deleteAddress()}
					text="Delete"
					theme={ThemeENUM.secondary}
					width="50"
				/>
				<Button
					font={font}
					kind={kindENUM.border}
					onClick={() => cancel()}
					text="Cancel"
					theme={ThemeENUM.secondary}
					width="50"
					id="cancel-delete-address"
				/>
			</ButtonWrapper>
		</DeleteAddressModalContainer>
	);
};

export default DeleteAddressModal;