import React from "react";
import { LKCashLeftSection, LKCashOfferText, LKCashPrimaryText, LKCashRightSection, LKCashWrapper, RemoveText, RightArrowIcon } from "./styles";
import { LKCashComponentTypes } from "./types";
import * as Icons from "../../../../Icons";


const LKCashComponent = ({ primaryText, primaryOfferText, lkCashApplied, onApplyClick, onRemoveClick }: LKCashComponentTypes) => {
	return (
		<LKCashWrapper onClick={!lkCashApplied ? () => onApplyClick() : () => null}>
			<LKCashLeftSection>
				<LKCashPrimaryText lkCashApplied={lkCashApplied}>
					{primaryText}
				</LKCashPrimaryText>
				<LKCashOfferText lkCashApplied={lkCashApplied}>
					{lkCashApplied && (<Icons.Check />)}{primaryOfferText}
				</LKCashOfferText>
			</LKCashLeftSection>

			<LKCashRightSection>
				{lkCashApplied ? (
					<RemoveText onClick={onRemoveClick}>Remove</RemoveText>
				) : (
					<RightArrowIcon onClick={onApplyClick}>
						<Icons.RightArrow />
					</RightArrowIcon>
				)}
			</LKCashRightSection>
		</LKCashWrapper>
	);
};

export { LKCashComponent };
