import React from "react";

import { SectionWrapper, Section, SectionName, SectionContent } from "./CommonSection.styles";
import { CommonSectionTypes } from "./CommonSection.types";


export default function CommonSection({ src,name,content,borderVisible, getSubmitFunction, methodData }:CommonSectionTypes){
	const clickHandler = ()=>{
		if(getSubmitFunction && methodData){
			getSubmitFunction(methodData.key)(methodData.code,methodData.gatewayId||null);
		}
	};

	return <SectionWrapper borderVisible={borderVisible} onClick={clickHandler} className="sectionPayWrapper">
		{src && <img src={src}/>}
		<Section isImage={!!src}>
			<SectionName>{name}</SectionName>
			<SectionContent>{content}</SectionContent>
		</Section>
		<img
			alt="right arrow"
			src="https://static1.lenskart.com/media/desktop/img/DesignStudioIcons/RightArrow.svg"
		/>
	</SectionWrapper>;
}
