import React, { useRef } from "react";
import { PaymentFailureAnimationLottieTypes } from "./PaymentFailureAnimation.types";
import Lottie from "lottie-react";
import AnimationLottie from "../../../../../../../assets/Animation/PaymentFailureAnimation.json";
const options = {
	loop: false,
	autoplay: true,
	animationData: AnimationLottie,
};
const style = {
	margin: "-2px",
	borderRadius:"50%",
};

const PaymentFailureAnimationLottie = ({
	getCallbackValue,
}: PaymentFailureAnimationLottieTypes) => {

	const ref = useRef(null);
	return <div>
		<Lottie
			lottieRef={ref}
			{...options}
			style={style}
			onComplete={() => {
				getCallbackValue(true);
			}}
		/>
	</div>;
};

export { PaymentFailureAnimationLottie };

