import { DataType, TypographyENUM } from "../../../../Types/general";

export interface SavedAddressType{
	dataLocale:any;
	font:TypographyENUM;
    address: Address;
    editAddress: (id: number)=>null;
    deleteAddress: (id: number) => null;
    selectAddress: (id: number) => null;
    isSelected: boolean;
    isPostcode: boolean;
    configData: DataType;
}

export type Address={
    addressline1: string;
    addressline2: string;
    city: string;
    country: string;
    state: string;
    postcode: string;
    phone: string;
    firstName: string;
    lastName: string;
    addressLabel: string;
    expectedDelivery?: string; // keeping this as optional as we don't get this from the api yet.
    id:number;
}

