import React from "react";
import { Title, TopSectionWrapper } from "./styles";
import { TopSectionTypes } from "./TopSection.types";

function TopSection({ heading, font, fontSmall, showChildren, children, disableTopPadding, className, isAddressPage, isRTL }: TopSectionTypes) {
	return (
		<TopSectionWrapper className={className} disableTopPadding={disableTopPadding} isRTL={isRTL}>
			<Title isAddressPage={isAddressPage} font={font} fontSmall={fontSmall}>{heading}</Title>
			{showChildren && <div>{children}</div>}
		</TopSectionWrapper>
	);
}

export { TopSection };
