import { FilterItemContainer, FilterItemWrapper, FilterItemImageWrapper, ImageSection, ImageItem, ImageTextSection, ImageText } from "./styles";
import { FilterItemTypes } from "./FilterItem.types";
import { ComponentSizeENUM } from "../../../Types/general";
import React, { useEffect } from "react";
/**
 * Default FilterItem Component
 */
const FilterItem = ({
	id,
	disabled = false,
	checked = false,
	onClick = () => null,
	componentSize = ComponentSizeENUM.medium,
	imageUrl,
	title,
}: // font = TypographyENUM.sans,
FilterItemTypes) => {
	const [selected, setSelected] = React.useState(checked);

	useEffect(() => {
		setSelected(checked);
	}, [checked]);

	const toggleCheckbox = (event: any) => {
		setSelected(!selected);
		onClick(event);
	};
	return (
		<FilterItemContainer id={id}>

			<FilterItemWrapper
				data-testid="FilterItem-test"
				styleSize={componentSize}
				type="checkbox"
				// onClick={toggleCheckbox}
				disabled={disabled}
				checked={selected}
				onChange={toggleCheckbox}
				value="Full Frame"
			></FilterItemWrapper>

			<FilterItemImageWrapper className="image">
				<ImageSection>
					<ImageItem src={imageUrl} />
				</ImageSection>
				<ImageTextSection>
					<ImageText>{title}</ImageText>
				</ImageTextSection>
			</FilterItemImageWrapper>

		</FilterItemContainer>
	);
};

export default FilterItem;
