import React from "react";
import * as Icons from "../../../../Icons/index";
import { AccordionTypes } from "./Accordion.types";
import {
	AccordionWrapper,
	TitleBox,
	TitleWrapper,
	Title,
	Content,
	Icon,
	BtnText,
	BtnWrapper,
	Image,
	StatusIcon
} from "./styles";

const Accordion = ({
	children,
	title,
	isMaxHeight,
	expand,
	handleExpand,
	showBorderTop,
	canCollapse,
	font,
	isRTL = false,
	btnText,
	icon,
	backgroundColor,
	color,
	titleFontSize,
	stylePadding,
	iconColor,
	contentColor,
	borderBottomStyle,
	contentPadding,
	contentBgColor,
	headerNavIcon,
	isHeaderStyle,
	width,
	deviceSpecificIssue=false
}: AccordionTypes) => {
	return (
		<AccordionWrapper isHeaderStyle={isHeaderStyle} borderBottomStyle={borderBottomStyle} borderTopStyle={showBorderTop} styleFont={font} isRTL={isRTL} backgroundColor={backgroundColor} color={color}>
			<TitleBox isHeaderStyle={isHeaderStyle} stylePadding={stylePadding} onClick={() => handleExpand()} expand={expand} isRTL={isRTL}>
				<TitleWrapper>
					{
						icon && <StatusIcon><Image src={icon} alt={"icon"} /></StatusIcon>
					}
					<Title width={width} isHeaderStyle={isHeaderStyle} isRTL={isRTL} color={color} fontSize={titleFontSize} fontWeight={600}>{title}</Title>
				</TitleWrapper>
				{canCollapse && <BtnWrapper id="button-wrapper"><BtnText deviceSpecificIssue={deviceSpecificIssue} isRTL={isRTL}>{btnText} <Icon deviceSpecificIssue={deviceSpecificIssue} style={iconColor}>{!expand ? headerNavIcon ?<Icons.PlusIcon/>:<Icons.DownArrow /> : headerNavIcon ? <Icons.Minus/>:<Icons.UpArrow />}</Icon></BtnText></BtnWrapper>}
			</TitleBox>
			{expand && <Content isHeaderStyle={isHeaderStyle} contentBgColor={contentBgColor} contentPadding={contentPadding} contentColor={contentColor} isMaxHeight={isMaxHeight} isRTL={isRTL}>{children}</Content>}
		</AccordionWrapper>
	);
};

export default Accordion;
