import React, { useState } from "react";
import { DropDataType } from "./Footer.types";
import { FooterDrop, FooterDropContent, FooterDropLabel, FooterDropPadding } from "./styles";

const FooterDropMenu = (data: DropDataType) => {
	const [footerOpen, setFooterOpen] = useState(false);
	return (
		<FooterDrop>
			<FooterDropLabel onClick={() => setFooterOpen(fo => !fo)}>
				{data.label}
				<i aria-hidden="true">&#43;</i>
			</FooterDropLabel>
			<FooterDropContent isOpen={footerOpen}>
				<FooterDropPadding dangerouslySetInnerHTML={{ __html: data.content }} />
			</FooterDropContent>
		</FooterDrop>
	);
};

export default FooterDropMenu;
