import React from "react";
import { GridType } from "./Grid.types";
import { GridBox } from "./styles";

const Grid = (props: GridType) => {
	return (
		<GridBox columns={props.col} rows={props.rows} templateArea={props.gridTemplateArea} fixedColumns={props.fixedColumns} colWidths={props.colWidths} defaultColWidth={props.defaultColWidth} >
			{props.children}
		</GridBox>
	);
};

export default Grid;
