import React from "react";
import { clDataImg } from "../../mockValues3";
import { ImgLayoutType } from "./ImgLayout.types";
import { ImgLayoutContainer, KidsCard, KidsImage, KidsText } from "./styles";
const ImgLayout = ({ data }: ImgLayoutType) => {
	const menuData = data || clDataImg;
	return menuData && Array.isArray(menuData) ? (
		<ImgLayoutContainer>
			{menuData.map(mn => <KidsCard className="getGaData" key={mn.id} href={mn.url}>
				<KidsImage alt="person" src={mn.img} />
				<KidsText>{mn.text}</KidsText>
			</KidsCard>)}
		</ImgLayoutContainer>
	) : null;
};
export default ImgLayout;
