import React from "react";
import { clData } from "../../mockValues3";
import Grid from "../Grid";
import { HoverIcon, SubMenuHead, SubMenuLinkContainer, SubMenuLinkText } from "../Dropdown/styles";
import { CustomFlex } from "../../styles";
import { ListGridType } from "./ListGrid.types";
import * as Icons from "../../../../../Icons/index";

const ListGrid = ({ data, isRTL }: ListGridType) => {
	const menuData = data || clData;
	return menuData && Array.isArray(menuData) ? (
		<Grid rows={3}>
			{menuData?.map(hd => <React.Fragment key={hd.id}>
				<SubMenuHead>{hd.text}</SubMenuHead>
				<CustomFlex borderColor={hd.text ? "#dfdfdf" : "#fff"} />
				<CustomFlex column={true}>
					{hd?.links?.map(lk => <SubMenuLinkContainer key={lk?.id}>
						<SubMenuLinkText className="getGaData" href={lk?.url || "/"}>{lk.text}</SubMenuLinkText>
						<HoverIcon isRTL={isRTL}>
							{
								isRTL ? (<Icons.IconLeft/>) : (<Icons.IconRight/>)
							}
						</HoverIcon>
					</SubMenuLinkContainer>)}
				</CustomFlex>
			</React.Fragment>)}
		</Grid>
	) : null;
};

export default ListGrid;
