import React from "react";
import { homeTest } from "../../mockValues3";
import { StoreLayoutType } from "./StoreLayout.types";
import { CityContainer, CityImage, CityName, StoreButton, StoreContent, StoreHead, StoreImage, StoreImageContainer, StoreImageGroup, StoreLayoutHolder, StoreMainContainer } from "./styles";

const StoreLayout = ({ data }: StoreLayoutType) => {
	const menuData = data || homeTest;
	return menuData.singleImg ? (
		<StoreLayoutHolder>
			<StoreImageContainer href={menuData.img?.url}>
				<StoreImage src={menuData.img?.img} alt="Store" />
			</StoreImageContainer>
			<StoreMainContainer>
				<StoreHead>{menuData.head}</StoreHead>
				<StoreContent>{menuData.content.map(md => <React.Fragment key={md as string}>{md}<br/></React.Fragment>)}</StoreContent>
				<StoreButton className="getGaData storeBtn" href={menuData.button.link}>{menuData.button.text}</StoreButton>
			</StoreMainContainer>
		</StoreLayoutHolder>
	) : (
		<StoreLayoutHolder>
			<StoreMainContainer>
				<StoreHead>{menuData.head}</StoreHead>
				<StoreContent>{menuData.content.map(md => <React.Fragment key={md as string}>{md}<br/></React.Fragment>)}</StoreContent>
				<StoreButton className="getGaData storeBtn" href={menuData.button.link}>{menuData.button.text}</StoreButton>
			</StoreMainContainer>
			<StoreImageGroup>
				{menuData.imgs?.map(img => <CityContainer className="getGaData" key={img.city as string} href={img.url}>
					<CityImage src={img.img} alt="city" />
					<CityName>{img.city}</CityName>
				</CityContainer>)}
			</StoreImageGroup>
		</StoreLayoutHolder>
	);
};

export default StoreLayout;
