import React from "react";
import { BannerSectionType } from "./BannerSection.types";
import { BannerContainer, BannerImageWrapper, BannerImage } from "./styles";

const BannerSection = ({ id, images }: BannerSectionType) =>{
	return <BannerContainer id={id}>
		{images.map((image,index) =>
			<BannerImageWrapper key={index} href={image.redirectUrl} target="_blank">
				<BannerImage src={image.source} alt={image.altText}/>
			</BannerImageWrapper>
		)}
	</BannerContainer>;
};

export default BannerSection;
