import React from "react";
import { BannerWithTitleType } from "./BannerWithTitle.types";
import { BannerContainer, BannerTitle, BannerComponentWrapper } from "./styles";

const BannerWithTitle = ({ id, title, bannerComponent, componentWidth }: BannerWithTitleType) =>{
	return <BannerContainer id={id}>
		<BannerTitle>{title}</BannerTitle>
		<BannerComponentWrapper width={componentWidth}>
			{React.Children.map(bannerComponent, (child) => {
				if (React.isValidElement(child))
					return React.cloneElement(child);
			})}
		</BannerComponentWrapper>

	</BannerContainer>;
};

export default BannerWithTitle;
