import React from "react";
import { useState } from "react";
import { TypographyENUM } from "../../../../Types/general";
import Accordion from "../../Footer/FooterAccordion";
import { FooterAccordionType } from "./FooterAccordion.types";
import { ChildrenWrapper, FooterAccordionWrapper } from "./styles";

const FooterAccordion = ({ label, html, font = TypographyENUM.defaultBook, canCollapse = false, showBorderTop = false, isMaxHeight= false, isRTL= false, width, customCSS }: FooterAccordionType) => {
	const [expand, setExpand] = useState(false);
	return (
		<FooterAccordionWrapper width={width}>
			<Accordion titleFontSize={14} icon={""} iconColor={{ color: "#ffff" }} contentPadding="5px 5px 5px 5px" contentColor="#fff" btnText="" color="#ffff" contentBgColor="#000042" stylePadding="0 0 8px" backgroundColor="#000042" borderBottomStyle={!expand} headerNavIcon={true} title={label} expand={expand} handleExpand={() => setExpand(!expand)} font={font} canCollapse={canCollapse} showBorderTop={showBorderTop} isMaxHeight={isMaxHeight} isRTL={isRTL} >
				{customCSS ? <ChildrenWrapper dangerouslySetInnerHTML={{ __html: html + customCSS }} /> : <ChildrenWrapper dangerouslySetInnerHTML={{ __html: html }} /> }
			</Accordion>
		</FooterAccordionWrapper>
	);
};

export default FooterAccordion;
