import React from "react";
import { HappyCustomerType } from "./HappyCustomer.types";
import { HappyCustomerText, HappyCustomerWrapper, VideoFrame, VideoWrapper } from "./styles";

const HappyCustomer = ({ id, font, videoLinks }: HappyCustomerType) =>{

	return <HappyCustomerWrapper id={id} font={font}>
		<HappyCustomerText>
			MEET OUR HAPPY CUSTOMERS
		</HappyCustomerText>
		<VideoWrapper>
			{videoLinks.map((video, index)=> <VideoFrame key={index} src={video}></VideoFrame>)}
		</VideoWrapper>
	</HappyCustomerWrapper>;

};

export default HappyCustomer;
