import { DeviceTypes } from "../../../../Types/general";

export interface ImageCarouselType{
	id:string
	images:ImageType[]
	courselConfig: courselConfigType
	mobileView: boolean;
	showCategoryCard: (categoryCards: any, contentType?: string, cardCategory?: string) => void;
	isRTL?: boolean
	showArrow?: boolean
	deviceType?: DeviceTypes
	getGAEvent?: (obj:any) => void
	triggerSelectPromotion?: (obj:any) => void
}

interface ImageType{
	altText:string
	source:string
	redirectUrl:string;
	categoryCards: any;
	contentType?: string;
	cardCategory?: string;
}

interface courselConfigType {
	slidesToShow: number,
	autoPlay: boolean
	autoplaySpeed: number
	infinite?: boolean
}