import React from "react";
import Carousel from "../../../Common/Carousel";
import { ProductImageCarouselType } from "./ProductImageCarousel.types";
import { ImageWrapper, Image, ProductImageCarouselWrapper } from "./styles";

const ProductImageCarousel = ({ id, images, font, gaPromotionObj, triggerSelectPromotion }: ProductImageCarouselType) =>{
	const carouselRef = React.useRef(null);

	return <ProductImageCarouselWrapper id={id} font={font}>
		<Carousel slidesToShow={3} wrapperClassName="custom-zoom-image" carouselConfig={{ slidesToScroll:3, autoplay: false, autoplaySpeed: 5000 }} ref={carouselRef}>
			{images.map((image, index) => (
				<Carousel.Slide id={index} currentSlide={1} key={index}>
					<ImageWrapper href={image.url} onClick={()=>triggerSelectPromotion?.(gaPromotionObj || image)} data-creative-name={image?.creativeName || gaPromotionObj?.creativeName} data-promotion-id={image?.promotionId || gaPromotionObj?.promotionId} data-promotion-name={image?.promotionName || gaPromotionObj?.promotionName} data-itemlist-id={image?.itemlistId || gaPromotionObj?.itemlistId} data-itemlist-name={image?.itemlistName || gaPromotionObj?.itemlistName}>
						<Image className="getGaData" src={image.image} />
					</ImageWrapper>
				</Carousel.Slide>
			))}
		</Carousel>
	</ProductImageCarouselWrapper>;

};

export default ProductImageCarousel;
