import React from "react";
import { ComponentSizeENUM } from "../../../Types/general";
import { InputField, QtnWrapper, QtyContainer, QtyText } from "./styles";
import { inputs, LensQtyTypes } from "./LensQty.types";

const LensQty = ({
	placeholder,
	type = inputs.type.text,
	componentSize = ComponentSizeENUM.small,
	id,
	font,
	dataLocale,
	isRTL = false,
}: LensQtyTypes) => {
	const [inputValue, setInputValue] = React.useState("");

	function onChange(e: React.ChangeEvent<HTMLInputElement>) {
		setInputValue(e.target.value);
	}
	return (
		<QtnWrapper styleSize={componentSize} styleFont={font}>
			<QtyContainer isRTL={isRTL}>
				<QtyText>{dataLocale.QTY}</QtyText>
				<InputField
					id={id}
					type={type}
					onChange={onChange}
					placeholder={placeholder}
					value={inputValue}
					isRTL={isRTL}
				/>
			</QtyContainer>
		</QtnWrapper>
	);
};

export { LensQty };

