import React from "react";
import { AccountLeftConatinerDataType } from "./AccountInformation.types";
import { AccountLeftConatinerWrapper, UnOrderList, A, List } from "./styles";

const AccountLeftConatiner = ({ sidebarData } : AccountLeftConatinerDataType) => {
	return (<AccountLeftConatinerWrapper>
		{
			sidebarData && sidebarData.map((item:any, index: number) => {
				return (
					<UnOrderList key={`account${index}`}>
						<List><A href={`${window.location.origin + item.href}`}>{item.key}</A></List>
					</UnOrderList>);
			})
		}
	</AccountLeftConatinerWrapper>);
};

export default AccountLeftConatiner;