import React from "react";
import * as Icons from "../../../../../Icons/index";
import { DataType } from "../../../Header/Header.types";
import { Container } from "./HeaderMobile.styles";

export interface HeaderMobileTypes {
	dataLocale?:DataType,
	redirectToHome?: ()=> void,
	isRTL?: boolean
}

const HeaderMobile = ({ dataLocale, redirectToHome, isRTL } : HeaderMobileTypes) => {
	return (
		<Container isRTL={isRTL}>
			<span onClick={redirectToHome}><Icons.BackArrow /></span>
			<h1>{dataLocale?.MANAGE_NOTIFICATIONS || "MANAGE NOTIFICATIONS"}</h1>
		</Container>
	);
};

export default HeaderMobile;
