import React from "react";
import { NotificationTypes } from "./ManageNotificatio.types";
import { NotificationTypeWrapper, Label, SwitchContainer, NotificatioTextImageWrapper, NotificationLogo, Notificationtext, CmnToggleLabelWrapper } from "./styles";

const ManageNotificationType = ({ notificationText, image, clickedOnToggle, checked, mobileView }: NotificationTypes) => {
	return (
		<NotificationTypeWrapper mobileView={mobileView}>
			<Label htmlFor="toggleStatus" onClick={() => clickedOnToggle()}
			>
				<NotificatioTextImageWrapper>
					<NotificationLogo>
						{image}
					</NotificationLogo>
					<Notificationtext>{notificationText}</Notificationtext>
				</NotificatioTextImageWrapper>
				<SwitchContainer>
					<CmnToggleLabelWrapper
						checked={checked}
						data-testid="switch-label"
					/>
				</SwitchContainer>
			</Label>
		</NotificationTypeWrapper>
	);
};

export default ManageNotificationType; 
