/* eslint-disable react/jsx-no-duplicate-props */
import React from "react";
import { ReturnTitle, ExchangeInfoWrapper, ExchangeProductDes, ProductReturnWrapper, StatusLI } from "./ExchangeCurrentStatus.styles";
import { ExchangeCurrentStatusType } from "./ExchangeCurrentStatus.type";

const ExchangeCurrentStatus = (
	props: ExchangeCurrentStatusType) => {
	const {
		id,
		font,
		dataLocale,
		status,
	} = props;
	return (
		<ExchangeInfoWrapper id={id} styleFont={font}>
			<ProductReturnWrapper>
				<ReturnTitle>
					{dataLocale.PRODUCT_STATUS}
				</ReturnTitle>
				<ExchangeProductDes>
					{status?.map((item, index) => {
						return (
							<StatusLI key={index}><i>{item.icon}</i><strong>{item.title}</strong> <span>{item.date}</span></StatusLI>
						);
					})}
				</ExchangeProductDes>
			</ProductReturnWrapper>
		</ExchangeInfoWrapper>
	);
};

export { ExchangeCurrentStatus };
