import React from "react";
import * as Icons from "../../../../../Icons/index";
import { AccordionTypes } from "./Accordion.types";
import {
	AccordionWrapper,
	TitleBox,
	TitleWrapper,
	Title,
	Content,
	Icon,
	BtnText,
	BtnWrapper,
	Image,
	StatusIcon
} from "./Styles";

const Accordion = ({
	children,
	title,
	isMaxHeight,
	expand,
	handleExpand,
	showBorderTop,
	canCollapse,
	isComponent,
	font,
	isRTL = false,
	btnText,
	icon
}: AccordionTypes) => {
	return (
		<AccordionWrapper borderTopStyle={showBorderTop} styleFont={font} isRTL={isRTL}>
			<TitleBox styleComponent={isComponent} onClick={() => handleExpand()} expand={expand} isRTL={isRTL}>
				<TitleWrapper>
					{
						icon && <StatusIcon><Image src={icon} alt={"icon"} /></StatusIcon>
					}
					<Title isRTL={isRTL}>{title}</Title>
				</TitleWrapper>
				{canCollapse && <BtnWrapper><BtnText>{btnText} <Icon>{!expand ? <Icons.DownArrow stroke="#00bac6" /> : <Icons.UpArrow stroke="#00bac6" />}</Icon></BtnText></BtnWrapper>}
			</TitleBox>
			{expand && <Content isMaxHeight={isMaxHeight}>{children}</Content>}
		</AccordionWrapper>
	);
};

export default Accordion;