import React from "react";
import { AddPowerContainer, UL, Anchor, PrescriptionManually, PrescriptionManuallyList, PrescriptionText, SavedPrescription, UploadPrescription, RequestCallBack, SendViaEmail, EamilPrescriptionList, CallPrescriptionList, SavedPrescriptionlist, UploadPrescriptionList } from "./styles";
import { AddPowerType } from "./AddPower.types";

const AddPower= ({ order, item, dataLocale, handleManuallprescription, handleSavedprescription, handleUploadprescription, handleCallBack, handleSendEmail }: AddPowerType) => {
	const handleManuallprescriptionFn = () => {
		handleManuallprescription(item?.productId, item?.prescription?.powerType, item, order);
	};
	const handleSavedprescriptionFn = () => {
		handleSavedprescription(item, order);
	};
	const handleUploadprescriptionFn = () => {
		handleUploadprescription(item, order);
	};
	const handleCallBackFn = () => {
		handleCallBack(item, order);
	};
	const handleSendEmailFn = () => {
		handleSendEmail(item, order);
	};
	return (
		<AddPowerContainer>
			<UL>
				<PrescriptionManuallyList onClick={() => handleManuallprescriptionFn()}>
					<Anchor>
						<PrescriptionManually></PrescriptionManually>
						<PrescriptionText>{dataLocale.ENTER_PRESCRIPTION_MANAULLY}</PrescriptionText>
					</Anchor>
				</PrescriptionManuallyList>
				<SavedPrescriptionlist onClick={() => handleSavedprescriptionFn()}>
					<Anchor>
						<SavedPrescription></SavedPrescription>
						<PrescriptionText>{dataLocale.USE_SAVED_PRESCRIPTION}</PrescriptionText>
					</Anchor>
				</SavedPrescriptionlist>
				<UploadPrescriptionList onClick={() => handleUploadprescriptionFn()}>
					<Anchor>
						<UploadPrescription></UploadPrescription>
						<PrescriptionText>{dataLocale.UPLOAD_PRESCRIPTION}</PrescriptionText>
					</Anchor>
				</UploadPrescriptionList>
				<CallPrescriptionList onClick={() => handleCallBackFn()}>
					<Anchor>
						<RequestCallBack></RequestCallBack>
						<PrescriptionText>{dataLocale.REQUEST_CALL_BACK}</PrescriptionText>
					</Anchor>
				</CallPrescriptionList>
				<EamilPrescriptionList onClick={() => handleSendEmailFn()}>
					<Anchor>
						<SendViaEmail></SendViaEmail>
						<PrescriptionText>{dataLocale.SEND_VIA_EMAIL}</PrescriptionText>
					</Anchor>
				</EamilPrescriptionList>
			</UL>
		</AddPowerContainer>
	);
};

export default AddPower;