import React from "react";
import { completePaymentType } from "./CompleteYourPayment.types";
import { CompletePaymentheader, CompltePaymentWrapper, CompletePaymentText, CompltePaymentSubText, PayNowButton } from "../styles";

const CompletePayment = ({ pendingStatus, payNow, dataLocale, handleConfirmOrder, handleCompletePayment, mobileView }: completePaymentType) => {
	const handleCompleteOrder = () => {
		handleConfirmOrder();
	};
	const handlePayNow = () => {
		handleCompletePayment();
	};
	return (
		<CompltePaymentWrapper mobileView={mobileView}>
			{pendingStatus === "pendingPayment" && <>
				<CompletePaymentheader>
					<CompletePaymentText mobileView={mobileView}>{payNow.header}</CompletePaymentText>
					<CompltePaymentSubText mobileView={mobileView}>{payNow.description}</CompltePaymentSubText>
				</CompletePaymentheader>
				<PayNowButton mobileView={mobileView} onClick={() => handlePayNow()}>{dataLocale.PAY_NOW}</PayNowButton>
			</>}
			{pendingStatus === "cod" && <>
				<CompletePaymentheader>
					<CompletePaymentText mobileView={mobileView}>{payNow.header}</CompletePaymentText>
					<CompltePaymentSubText mobileView={mobileView}>{payNow.description}</CompltePaymentSubText>
				</CompletePaymentheader>
				<PayNowButton mobileView={mobileView} onClick={() => handleCompleteOrder()}>{dataLocale.CONFIRM_ORDER}</PayNowButton>
			</>}
		</CompltePaymentWrapper>
	);
};

export default CompletePayment;
