import React from "react";
import { Div, Span } from "../styles";
import { DelieveryAddressType } from "./DelieveryAddress.types";
import { AddressWrapper, DeliveryAddressText, NameText, CompleteAddressWrapper, Email, PhoneText } from "./styles";


const DeliveryAddress = ({ shippingAddress, dataLocale, isRTL=false }: DelieveryAddressType) => {
	const { DELIVERY_ADDRESS, INDIA } = dataLocale;
	const { firstName = "", lastName = "", phone, addressline1, addressline2, landmark, city, postcode, state, country, email, phoneCode } = shippingAddress || {};
	return <AddressWrapper>
		<DeliveryAddressText>{DELIVERY_ADDRESS}</DeliveryAddressText>
		<NameText>{firstName} {lastName}</NameText>
		<CompleteAddressWrapper>
			{addressline1 ? <Div>{addressline1},</Div> : ""}
			{addressline2 ? <Div>{addressline2},</Div> : ""}
			{landmark ? <Div>{landmark},</Div> : ""}
			{city ? <Span>{city}</Span> : ""}
			{postcode ? (
				<Span>
					{city ? " - " : ""}
					{postcode},
				</Span>
			) : (
				""
			)}
			<br />
			{state ? <Span>{state}, </Span> : ""}
			{country ? <Span>{country === "IN" ? INDIA : country}</Span> : ""}
		</CompleteAddressWrapper>
		<Email>{email}</Email>
		<PhoneText isRTL={isRTL}>{phoneCode}-{phone}</PhoneText>
	</AddressWrapper>;
};

export default DeliveryAddress;