import React from "react";
import { CardType } from "./SavedCards.types";
import { CardImage, CardWrapper, Image, CardDetail, CardNameDeleteIconWrapper, CardName, DeleteIcon, CardNumberExpiry, CardNumber, CardExpiry, CardImageConatiner } from "./styles";

const Card = ({ cardBrand, cardMode, cardToken, expiryMonth, expiryYear, cardNumber, onDeleteCard, dataLocale }: CardType) => {
	return (<CardWrapper>
		<CardImageConatiner>
			<CardImage />
		</CardImageConatiner>
		<CardDetail>
			<CardNameDeleteIconWrapper>
				<CardName>{cardBrand} {cardMode}</CardName>
				<DeleteIcon onClick={() => onDeleteCard(cardToken)}>
					<Image src="https://static.lenskart.com/media/desktop/img/delete.png" alt="image" />
				</DeleteIcon>
			</CardNameDeleteIconWrapper>
			<CardNumberExpiry>
				<CardNumber>{cardNumber}</CardNumber>
				<CardExpiry>{dataLocale.CARD_EXPIRES_IN} {expiryMonth}/{expiryYear}</CardExpiry>
			</CardNumberExpiry>
		</CardDetail>
	</CardWrapper>);
};

export default Card;