import React from "react";
import Card from "./card.component";
import { SvaedCardTypes } from "./SavedCards.types";
import { SavedCardWrapper, Heading, AlertMessage } from "./styles";

const SavedCard = ({ savedCardList, onDeleteCard, dataLocale } : SvaedCardTypes) => {
	const { YOUR_SAVED_CARDS, YOU_DONT_HAVE_SAVED_CARDS } = dataLocale;
	return (<SavedCardWrapper>
		<Heading>{YOUR_SAVED_CARDS}</Heading>
		{savedCardList.length === 0 && <AlertMessage>{YOU_DONT_HAVE_SAVED_CARDS}</AlertMessage>}
		{
			savedCardList.length > 0 && savedCardList.map((card:any,index:number) => {
				return (
					<Card
						key={`savedcard_${index}`}
						cardBrand={card.cardBrand}
						cardMode={card.cardMode}
						cardToken={card.cardToken}
						expiryMonth={card.expiryMonth}
						expiryYear={card.expiryYear}
						cardNumber={card.number}
						expired={card.expired}
						onDeleteCard={onDeleteCard}
						dataLocale={dataLocale}
						payLaterAllowed={false}
					/>
				);
			})
		}
	</SavedCardWrapper>);
};

export default SavedCard;