import React from "react";
import { Container, Header, Name, Body, Email, Phone, Span } from "./OrderAddress.styles";
import { OrderAddressTypes } from "./OrderAddress.types";

const OrderAddress = ({ canUpdateAddress, customerName, address1, address2, city, pincode, state, country, email, phoneNumber, countryCode,dataLocale, setEditAddress }: OrderAddressTypes) => {
	return (
		<Container>
			<Header>
				<Span>{dataLocale?.DELIVERY_ADDRESS}: </Span>
				{canUpdateAddress && <span onClick={() => setEditAddress(true)} >Edit</span>}
			</Header>
			<Name>
				{customerName}
			</Name>
			<Body>
				<div>{address1}</div>
				{address2 && <div>{address2}</div>}
				<span>{city}</span>
				{pincode && <span> - {pincode}</span>}
				<br />
				<span>{state}</span>
				{country && <span>, {country.toLowerCase() === "ae" ? dataLocale?.UAE || "UAE" : country }</span>}
			</Body>
			{email && <Email>
				{email}
			</Email>}
			{phoneNumber && <Phone>
				{countryCode && <span>{countryCode}-</span>}
				<span>{phoneNumber}</span>
			</Phone>}
		</Container>
	);
};

export default OrderAddress;
