import React from "react";
import { Container } from "./ReturnCarousel.styles";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

export interface ReturnCarouselTypes {
	image1: string,
	image2: string,
}

const ReturnCarousel = ({ image1, image2 }: ReturnCarouselTypes) => {

	const settings = {
		dots: true,
		infinite: true,
		speed: 300,
		slidesToShow: 1,
		slidesToScroll: 1,
		arrows: false,
		mobileFirst: true,
	};

	return (
		<Container>
			<Slider className="slider" {...settings} >
				<div>
					<img src={image1} />
				</div>
				<div>
					<img src={image2} />
				</div>
			</Slider>
		</Container>
	);
};

export default ReturnCarousel;

