import React from "react";
import { Container, Body } from "./ReturnAddress.styles";
import { ReturnAddressTypes } from "./ReturnAddress.types";

const ReturnAddress = ({ address1, address2, city, pincode, state, changeHandler }: ReturnAddressTypes) => {
	return (
		<Container>
			<Body>
				<div>
					{address1}
					{address2 && <span>, {address2}</span>}
					{city && <span>, {city}</span>}
					{state && <span>, {state}</span>}
					{pincode && <span>, {pincode}</span>}
				</div>
				<div>
					<span onClick={changeHandler}>
						Change
					</span>
				</div>
			</Body>
		</Container>
	);
};

export default ReturnAddress;
