import React from "react";
import {
	DirectionTitle,
	Distance,
	Icon,
	Left,
	Right,
	StoreAddress,
	StoreData,
	StoreName,
	StoreTiming,
} from "./SelectStore.styles";

export default function SelectStoreCard({ store }: any) {
	function TimeFormatter(time: string):string {
		return (time.substring(0, time.length - 6) + " " + time.substring(time.length-2, time.length));
	}
	return (
		<StoreData>
			<Left>
				<StoreName>
					{store?.name}
				</StoreName>
				<StoreAddress>
					{store?.address}
				</StoreAddress>
				<StoreTiming>
					<strong>Timings: </strong>
					<span>
						{TimeFormatter(store?.openingTime)} - {TimeFormatter(store?.closingTime)} (All Days)
					</span>
				</StoreTiming>
			</Left>
			<Right onClick={() => window.location.href = store?.googleUrl}>
				<Icon>
					<img src="https://static.lenskart.com/media/mobile/universal/images/returnExchange/Directions.svg" alt="nav" />
				</Icon>
				<DirectionTitle>
												Directions
				</DirectionTitle>
				<Distance>
					{store?.distance?.humanReadable}
				</Distance>
			</Right>
		</StoreData>
	);
}
