import React from "react";
import { Container, CardContainer, PriceContainer, Price, GST, Breakup } from "./TotalPriceCard.styles";
import { TotalPriceCardTypes } from "./TotalPriceCard.types";

const TotalPriceCard = ({ totalPrice, currency, viewPriceBreakupHandler, showGST }: TotalPriceCardTypes) => {
	return (
		<Container>
			<CardContainer>
				<PriceContainer>
					<Price>
						<span>{currency}</span> {totalPrice}
					</Price>
					{showGST && <GST>
						Total Including GST
					</GST>}
				</PriceContainer>
				<Breakup onClick={viewPriceBreakupHandler}>
					View Breakup
				</Breakup>
			</CardContainer>
		</Container>
	);
};

export default TotalPriceCard;
