export const ModalWrapper = styled.div`
	position: fixed;
    margin: 0 auto;
    top: 0;
    left: 0;
    padding-top: 10px;
    z-index: 12000 !important;
    overflow-x: hidden;
    overflow-y: hidden;
    height: 100vh;
	width: 100vw;
	background:rgba(0,0,0,0.5);
`;


export interface PayLaterType {
    dataLocale: DataType;
    handlePayLater: ()=>void;
    handlePayNow?: ()=>void;
	isPrimerOff?: boolean;
	isContactLensConsentEnabled?: boolean;
	isContactLensCheckboxChecked?: boolean;
}

// handlePayNow will be passed from jus base component for other conditions close this modal on click
const PayLater = ({ dataLocale, handlePayLater, handlePayNow, isPrimerOff=false, isContactLensConsentEnabled, isContactLensCheckboxChecked } : PayLaterType) => {
	const [ isPayLaterModalActive, setIsPayLaterModalActive]=useState(false);
	const handlePayLaterClick = ()=>{
		handlePayLater();
	};
	const handlePayNowClick = ()=>{
		if(isPrimerOff && handlePayNow){
			handlePayNow();
			return;
		}
		setIsPayLaterModalActive(false);
	};
	return <>
		{isPrimerOff? <Button id="pay-later" font={TypographyENUM.lkSansBold} kind={kindENUM.border}
			onClick={()=>setIsPayLaterModalActive(true)}
			text={dataLocale.PAY_LATER}
			theme={ThemeENUM.secondary}
			fontSize={20}
			width="100"
			disabled={isContactLensConsentEnabled && !isContactLensCheckboxChecked}
		/> :<PayNowBtn
			onClick={()=>setIsPayLaterModalActive(true)}
			font={TypographyENUM.lkSansBold}
			disabled={isContactLensConsentEnabled && !isContactLensCheckboxChecked}
		>
			<span>{dataLocale.PAY_LATER}</span>

		</PayNowBtn>}
		{isPayLaterModalActive &&
		<ModalWrapper>
			<PayLaterModal>
				<PayLaterContainer>
					<CloseButton >
						<Cross onClick={()=>setIsPayLaterModalActive(false)} />
					</CloseButton>
					<img alt="img" src="https://static.lenskart.com/media/mobile/images/card.svg"></img>
					<PayLaterHeader>
						Not Ready to Pay Yet?
					</PayLaterHeader>
					<PayLaterDescription>
						Visit the showroom first and complete payment after your consultation.
						<div>Note: Your frame selection is not guaranteed until payment is made</div>
					</PayLaterDescription>
					<PayLaterSubText>
						Do you still wish to pay later?
					</PayLaterSubText>
					<PayLaterButtonContainer>
						<PayLaterButtonPrimary onClick={handlePayLaterClick}>Yes, continue</PayLaterButtonPrimary>
						<PayLaterButtonSecondary onClick={handlePayNowClick}>No, I&apos;ll pay for my order now</PayLaterButtonSecondary>
					</PayLaterButtonContainer>
				</PayLaterContainer>
			</PayLaterModal>
		</ModalWrapper>}
	</>;
};

export { PayLater };
