import React, { useState } from "react";
import { InputContainer, InputTextField, PlaceholderText } from "./InputField.styles";
import { inputFieldType } from "./InputField.types";

const InputField = ({ placeholderText } : inputFieldType) => {

	const [animate, SetAnimate] = useState(false);

	const changeHandler = (e: { target: { value: string; }; }) => {
		if(e.target.value === "") SetAnimate(false);
		else SetAnimate(true);
	};

	return (
		<InputContainer>
			<InputTextField animate={animate} type="text" onChange={changeHandler}></InputTextField>
			<PlaceholderText animate={animate}>{placeholderText}</PlaceholderText>
		</InputContainer>
	);
};

export default InputField;
