import React from "react";
import { InfoContainer, Code, CreditContainer, Currency, Credit, ActionButton } from "./StoreInfoBar.styles";
import { StoreInfoBarType } from "./StoreInfoBar.types";

const StoreInfoBar = ({ code, currency, credit, buttonText, onActionClick }: StoreInfoBarType) => {
	return (
		<InfoContainer>
			<Code>{code}:</Code>
			<CreditContainer>
				<Currency>
					{currency}
				</Currency>
				<Credit>
					{credit}
				</Credit>
			</CreditContainer>
			<ActionButton onClick={onActionClick}>{buttonText}</ActionButton>
		</InfoContainer>
	);
};

export default StoreInfoBar;
