import React from "react";
import { SwatchContainer, SwatchTitle, CreditContainer, Currency, Credit } from "./StoreSwatch.styles";
import { StoreSwatchType } from "./StoreSwatch.types";

const StoreSwatch = ({ swatchTitle, currency, credit, highlight }: StoreSwatchType) => {
	return(
		<SwatchContainer highlight={highlight}>
			<SwatchTitle>
				{swatchTitle}:
			</SwatchTitle>
			<CreditContainer>
				<Currency>
					{currency}
				</Currency>
				<Credit>
					{credit}
				</Credit>
			</CreditContainer>
		</SwatchContainer>
	);
};

export default StoreSwatch;
