import React from "react";
// import InfoBar from "../../../../CommonComponents/InfoBar";

const BankOffer = (props:any) => {
	const {
		bankOffer,
		applyOffer,
		// frameProduct,
		// paymentCTA,
		walletOffer = false,
		dataLocale,
	} = props;
	const offer = bankOffer.offers[0];
	const bank = bankOffer;
	const payNow = () => {
		if (bank.bankcode) {
			applyOffer(offer.id, bank.code, bank.bankcode);
		} else {
			applyOffer(offer.id, bank.code, bank.gatewayId);
		}
	};
	return (
		<div className="banks-wrapper">
			{!walletOffer && (
				<div className="back" onClick={() => props.showBankList()}>
					<i aria-hidden="true" className="fa fa-angle-left fs20 margin-r5"></i>
					{dataLocale.BACK}
				</div>
			)}
			<div className="display-flex align-items-center margin-t20 margin-b20">
				{offer.logoImage && (
					<span className="bank-icon">
						<img alt={offer.label || offer.name} src={offer.logoImage} />
					</span>
				)}
				<span>
					<strong>{bank.label || bank.name}</strong>
					{offer.label && <span className="b-offer">{offer.label}</span>}
				</span>
			</div>
			{offer.bannerImage && (
				<div className="margin-b15">
					<img alt={offer.description} className="bd-info-img" src={offer.bannerImage} />
				</div>
			)}
			{offer.longDescription && (
				<div dangerouslySetInnerHTML={{ __html: offer.longDescription }}></div>
			)}
			{/* {frameProduct && <InfoBar frameProduct={frameProduct} paymentCTA={paymentCTA} />} */}
			<button className="bank-pay" onClick={() => payNow()}>
				{dataLocale.PLACE_ORDER}
			</button>
		</div>
	);
};

export default React.memo(BankOffer);
