import React, { ReactNode } from "react";
import { AboutEyePowerTypes } from "./AboutEyePower.types";
import { BottomSection, CheckBox, EyeBoxContainer, Flex, HighlightsSection, InfoSection, MainSection, TipFlex, Title } from "./AboutEyePowerbox.styles";

const Tip = ({ src, children, isRTL }: {src: string, children: ReactNode, isRTL: boolean}) => {
	return (
		<TipFlex>
			<CheckBox isRTL={isRTL} src={src} alt="" />
			{children}
		</TipFlex>
	);
};
