import React from "react";
import { CLInfo, CLOptionCard, CLOptionCardWrapper, CLOptionImg, CLOptionLabel, ContactLensOptionsWrapper } from "./ContactLens.styles";


interface ContactLensColorOptionsType{
	options:any;
	onSelectContactLensColor:(index:number)=>void,
	dataLocale:any
}

const ContactLensColorOptions = ({ options,onSelectContactLensColor,dataLocale }:ContactLensColorOptionsType) =>{
	const data = options?.additionalOptions;
	const { TAP_ON_CARDS_VIEW_LENS } = dataLocale;	
	return (
		
		<ContactLensOptionsWrapper>
			<CLInfo>
				{TAP_ON_CARDS_VIEW_LENS??"Tap on cards to view the lens"}
			</CLInfo>
			<CLOptionCardWrapper>
				{
					data?.options[0]?.optionList.map((item:any,index:number)=>
						<CLOptionCard key={item.id} isSelected={item.id==data?.options[0]?.selectedId??false} onClick={()=>onSelectContactLensColor(index)}>
							<CLOptionImg src={item?.imageUrl}/>
							<CLOptionLabel>
								{item?.title} {`(${data?.options[1]?.optionList[0]?.title}${data?.options[1]?.label})`}
							</CLOptionLabel>
						</CLOptionCard>)
				}
			</CLOptionCardWrapper>
		</ContactLensOptionsWrapper>
	);
};

export { ContactLensColorOptions };