import React from "react";
import ProductImage from "../../ProductImage";
import { ImagesGridProps } from "./ImagesGrid.types";
import { GridContainer, GridItem } from "./ImagesGrid.styles";

function ImagesGrid({
	imagesList,
	itemClassName="grid-image-item",
	containerClassName="grid-item-container",
	containerId="",
	onClick,altText, }: ImagesGridProps) {
	return (
		<GridContainer itemClassName={itemClassName} containerClassName={containerClassName} containerId={containerId}>
			{imagesList.map(({ imageUrl, id }, idx) => (
				<GridItem key={idx} itemId={id?.toString() || idx.toString()}>
					<ProductImage altText={altText+` ${(idx)%2==0?"left side":"right side"}`} imageSrc={imageUrl} onClick={() => id !== undefined && id >= 0 && onClick(id)} />
				</GridItem>))}
		</GridContainer>
	);
}

export { ImagesGrid };
