import React from "react";
import { DataType } from "../../../../../Types/general";
import { ItemDeliveryWrapper, Text } from "./ItemDelivery.styles";

const ItemDelivery = ({ localeData, productClassification } : {
	localeData: DataType,
	productClassification: string
}) => {

	const { DELIVERY_MSITE_TEXT, DAYS_TEXT_FRAME, DAYS_TEXT_CONTACT } = localeData;
	return (
		<ItemDeliveryWrapper>
			<img alt="icon" src="https://static.lenskart.com/media/mobile/img/Frame.png" />

			<div>
				<Text color="secondary">{DELIVERY_MSITE_TEXT} </Text>
				<Text>
					{productClassification === "contact_lens" ? DAYS_TEXT_CONTACT : DAYS_TEXT_FRAME}
				</Text>
			</div>
		</ItemDeliveryWrapper>
	);
};


export default ItemDelivery;