import React from "react";
import { ComponentSizeENUM, TypographyENUM } from "../../../../../Types/general";
import { Rating } from "../../../../Common/Rating/Rating.component";
import { RatingValue, ReviewMobileWrapper, SideContent, Text } from "./ReviewMobile.styles";
import { ReviewMobileTypes } from "./ReviewMobile.types";

const ReviewMobile = (props: ReviewMobileTypes) => {
	const { isReadOnly = true, rating = 4.5, maxRating = 5, componentSize = ComponentSizeENUM.small, onStarsChange,
		isRTL, id, numOfRatings, font = TypographyENUM.sans } = props;
	return (
		<ReviewMobileWrapper font={font}>
			<RatingValue>
				{rating}
			</RatingValue>
			<SideContent>
				<Rating id={id} isReadOnly={isReadOnly} rating={rating} maxRating={maxRating} componentSize={componentSize} onStarsChange={onStarsChange} isRTL={isRTL} />
				<Text>
					{numOfRatings} Reviews
				</Text>
			</SideContent>
		</ReviewMobileWrapper>
	);
};

export default ReviewMobile;