import React from "react";
import SvgStar from "../../../../../Icons/Star";
import { TypographyENUM } from "../../../../../Types/general";
import { ProgressBarPercentage, ProgressBarRatings, ReviewMobileProgressBar, ReviewProcessBar, ReviewProcessBarWrapper, StarCount } from "./ReviewMobileProgressBar.styles";
import { ReviewMobileProgressBarTypes } from "./ReviewMobileProgressBar.types";

const ReviewProgressBar = (props: ReviewMobileProgressBarTypes) => {
	const { font = TypographyENUM.sans, percentage, stars } = props;
	return (
		<ReviewMobileProgressBar font={font}>
			<ProgressBarRatings>
				<StarCount>{stars}</StarCount> <SvgStar fill="#000" />
			</ProgressBarRatings>
			<ReviewProcessBarWrapper>
				<ReviewProcessBar style={{ width: `${percentage}%` }}></ReviewProcessBar>
			</ReviewProcessBarWrapper>
			<ProgressBarPercentage>
				{percentage}%
			</ProgressBarPercentage>
		</ReviewMobileProgressBar>
	);
};

export default ReviewProgressBar;
