import React from "react";
import * as Icons from "../../../../../Icons/index";
import { Container, SocialShareWrapper, Subtext, Text } from "./SocialShare.styles";
import { SocialShareTypes } from "./SocialShare.types";

const SocialShare = ({ dataLocale, handleClick, config }: SocialShareTypes) => {
	const { SHARE_THIS_PRODUCT, WHATSAPP, FACEBOOK, TWITTER } = dataLocale;
	return (
		<SocialShareWrapper>
			<Text>{SHARE_THIS_PRODUCT}</Text>
			{(config ? config.showWhatsapp : true) && (<Container onClick={() => handleClick("whatsapp")}>
				<Icons.WhatsappSocialShare height={24} width={24} />
				<Subtext>{WHATSAPP}</Subtext>
			</Container>)}
			{(config ? config.showFacebook : true) && (<Container onClick={() => handleClick("facebook")}>
				<Icons.FacebookSocialShare height={24} width={24} />
				<Subtext>{FACEBOOK}</Subtext>
			</Container>)}
			{(config ? config.showTwitter : true) && (<Container onClick={() => handleClick("twitter")}>
				<Icons.TwitterSocialShare height={24} width={24} />
				<Subtext>{TWITTER}</Subtext>
			</Container>)}
		</SocialShareWrapper>
	);
};

export default SocialShare;
