import React from "react";
import { TypographyENUM } from "../../../../Types/general";
import { NearStoreType } from "./nearStore.types";
import { StoreImage, StoreInfo, StoreInfoLink, StoreInfoText, StoreWrapper } from "./nearStore.styles";

const NearStore = ({
	id="store-info-card",
	storeImg = "https://static.lenskart.com/media/desktop/img/pdp/visit_store.png",
	storeInfo = "Please Share Your Location To See Nearby Stores",
	font = TypographyENUM.defaultBook,
	onClickStoreLocator,
	isRTL = false
}: NearStoreType) => {
	return (
		<StoreWrapper id={id} styleFont={font} isRTL={isRTL}>
			<StoreImage src={storeImg} alt="store" />
			<StoreInfo isRTL={isRTL}>
				<StoreInfoText>{storeInfo}</StoreInfoText>
				<StoreInfoLink href={onClickStoreLocator} target="_blank">Store Locator</StoreInfoLink>
			</StoreInfo>
		</StoreWrapper>
	);
};

export { NearStore } ;
